import React, { useState } from 'react'
import axios from 'axios';
import {navigate, Link} from '@reach/router'

    // eslint-disable-next-line
export default (props) => {
    const { initialName, onSubmitProp } = props;
    //keep track of what is being typed via useState hook
    const [name, setName] = useState(initialName); 
    const [genre, setGenre] = useState("fiction"); 
    // const [errors, setErrors] = useState([]);
    //handler when the form is submitted
    const onSubmitHandler = e => {
        //prevent default behavior of the submit
        e.preventDefault();
        //make a post request to create a new person
        onSubmitProp({name, genre});
        setName("");
    }

    return (

        <form className="formGroup" onSubmit={onSubmitHandler}>

            <div className="form-group">
                <label htmlFor="name">Name:</label>
                <input type="text" 
                onChange={(e) => setName(e.target.value)} 
                value={name} 
                className="form-control" id="name" placeholder="ex) Ernest Hemingway" />

                <select onChange={(e)=>setGenre(e.target.value)} name="select" >
                    <option value="essay" >essay</option>
                    <option value="memoir">memoir</option>
                    <option value="mystery">mystery</option>
                    <option value="sci-fi">sci-fi</option>
                    <option value="poery">poery</option>
                    <option value="crime/detective">crime/detective</option>
                </select>
            </div>

            {/*       
      DROPDOWN MENU 
      <select onChange={(e)=>setSelect(e.target.value)} name="select" >
                    <option value="people" >People</option>
                    <option value="planet">Planet</option>
                </select> */}
            <div>
                <button className="btn btn-primary"><Link style={{"color": "white"}} to='/'> Cancel </Link></button>
                <button type="submit" className="btn btn-primary">Submit</button>
            </div>
        </form>

    )
}

