import React, { useEffect, useState } from 'react'
import { Link } from '@reach/router'
import axios from 'axios';
import AuthorForm from '../components/AuthorForm'
import AuthorList from '../components/AuthorList';

// eslint-disable-next-line
export default () => {
    const [ authors, setAuthors ] = useState([]);
    const [loaded, setLoaded] = useState(false);
    //Create an array to store errors from the API
    const [errors, setErrors] = useState([]); 


    useEffect(()=>{
        axios.get("http://localhost:8001/api/authors")
            // .then(res=>setProducts(res.data.products)) if res.json({products: allProducts}) instead of getting it as an array from controller
            .then(res=>{
                setAuthors(res.data)
                setLoaded(true);
            })  
            .catch(err=>console.log("Error: ", err))     
    }, [])

    const createProduct = product => {
        axios.post('http://localhost:8001/api/authors/new', product)
            .then(res=>{
                console.log(res, "** New author created")
                //updating state after api call => re-render the page; show the new product created without having to refresh the page
                setAuthors([...authors, res.data]);
            })
            .catch(err=>{
                const errorResponse = err.response.data.errors; // Get the errors from err.response.data
                const errorArr = []; 
                for (const key of Object.keys(errorResponse)) { // Loop through all errors and get the messages
                    errorArr.push(errorResponse[key].message)
                }
                setErrors(errorArr);
            })  

    // const removeFromDom = authorId => {
    //     setAuthors(authors.filter(auth => auth._id !== authorId));
    //     console.log(authors.filter(auth => auth._id !== authorId));
    // }

    return (

        <div className="mainPage">
            <h1>Favorite Authors</h1>

            <h5><Link to='/new'> Add an author </Link></h5>

            <table class="table table-bordered">
            <thead>
                <tr>
                <th scope="col">First</th>
                <th scope="col">Last</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                <td>Paul Auster</td>
                <td>
                    <button type="button" class="btn btn-outline-success">Success</button>
                    <button type="button" class="btn btn-outline-warning">Danger</button>
                </td>
                </tr>
                <tr>
                <td>Cheryl Strayed</td>
                <td>
                    <button type="button" class="btn btn-outline-success">Success</button>
                    <button type="button" class="btn btn-outline-warning">Danger</button>
                    </td>
                </tr>
            </tbody>
            </table>

        </div>
    )
    }
}
