import React, { useState } from 'react'
import axios from 'axios';
// import { Link, navigate } from '@reach/router';

// eslint-disable-next-line
export default (props) => {
    const { initialName } = props;
    //keep track of what is being typed via useState hook
    const [ authors, setAuthors ] = useState([]);
    const [name, setName] = useState(initialName);
    const [errors, setErrors] = useState([]); 


    const createAuthor = author => {
    axios.post('http://localhost:8001/api/authors/new', author)
        .then(res=>{
            console.log(res, "** New author created")
            //updating state after api call => re-render the page; show the new product created without having to refresh the page
            setAuthors([...authors, res.data]);
        })
        .catch(err=>{
            const errorResponse = err.response.data.errors; // Get the errors from err.response.data
            const errorArr = []; 
            for (const key of Object.keys(errorResponse)) { // Loop through all errors and get the messages
                errorArr.push(errorResponse[key].message)
            }
            setErrors(errorArr);
        })  

    //handler when the form is submitted
    const onSubmitHandler = e => {
        e.preventDefault();
        //creating onSubmit method that takes in name as an object
        createAuthor({ name });
        setName("");
    }

    return (

        <div>

            {/* <h1>Favorite Authors</h1>

            <h5><Link to='/'> Home </Link></h5>
            <h4>Add a new author:</h4> */}

            {errors.map((err, index) => <p style={{"color": "red"}} key={index}> {err} </p>)}

            <form className="formGroup" onSubmit={onSubmitHandler}>
                <div className="form-group">
                    <label HtmlFor="name">Name:</label>
                    <input type="text" 
                    onChange={(e) => setName(e.target.value)} 
                    value={name} 
                    className="form-control" id="name" placeholder="ex) Hemmingway" />
                </div>
                <div>
                    <button class="btn btn-primary">Cancel</button>
                    <button type="submit" class="btn btn-primary">Submit</button>
                </div>
            </form>
        </div>
    )
}
}
